import { TOGGLE_CART_HIDDEN } from './cartConstants'

const INITIAL_STATE = {
	hidden: true,
}

const cartReducer = (state = INITIAL_STATE, action) => {
	const { type } = action

	switch (type) {
		case TOGGLE_CART_HIDDEN:
			return {
				...state,
				hidden: !state.hidden,
			}

		default:
			return state
	}
}

export default cartReducer
