import React from 'react'
import { Route } from 'react-router-dom'
import { createStructuredSelector } from 'reselect'
import { connect } from 'react-redux'

import CollectionsOverviewContainer from '../../components/collections-overview/CollectionsOverviewContainer'
import CollectionScreen from '../collection/CollectionScreen'

import { fetchCollectionsStartAsync } from '../../redux/shop/shopActions'
import { selectIsCollectionLoaded } from '../../redux/shop/shopSelectors'

import WithSpinner from '../../components/withSpinner/WithSpinner'

const CollectionScreenWithSpinner = WithSpinner(CollectionScreen)

class ShopScreen extends React.Component {
	componentDidMount() {
		const { fetchCollectionsStartAsync } = this.props
		fetchCollectionsStartAsync()
	}

	render() {
		const { match, isCollectionsLoaded } = this.props
		return (
			<div className='shop-screen'>
				<Route
					exact
					path={`${match.path}`}
					component={CollectionsOverviewContainer}
				/>
				<Route
					path={`${match.path}/:collectionName`}
					render={(props) => (
						<CollectionScreenWithSpinner
							isLoading={!isCollectionsLoaded}
							{...props}
						/>
					)}
				/>
			</div>
		)
	}
}

const mapStateToProps = createStructuredSelector({
	isCollectionsLoaded: selectIsCollectionLoaded,
})

const mapDispatchToProps = (dispatch) => ({
	fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync()),
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopScreen)
